document.addEventListener('DOMContentLoaded',function(){let words=['Open','Effortless','Custom'];let currentWordIndex=0;const highlightElement=document.querySelector('.mdx-hero__tag-highlight');function changeWord(){highlightElement.classList.add('mdx-hero__tag-highlight--fade-out');setTimeout(()=>{currentWordIndex=(currentWordIndex+1)%words.length;highlightElement.textContent=words[currentWordIndex];highlightElement.classList.remove('mdx-hero__tag-highlight--fade-out');},500);}
setInterval(changeWord,6000);});