document.addEventListener("DOMContentLoaded",function(){if(document.forms.search){var query=document.forms.search.query
query.addEventListener("blur",function(){if(this.value)zaraz.track("search",{search_term:this.value})})}
document$.subscribe(function(){var feedback=document.forms.feedback
if(typeof feedback==="undefined")return
for(var button of feedback.querySelectorAll("[type=submit]")){button.addEventListener("click",function(ev){ev.preventDefault()
var page=document.location.pathname
var data=this.getAttribute("data-md-value")
zaraz.track("feedback",{page,data})
feedback.firstElementChild.disabled=true
var note=feedback.querySelector(".md-feedback__note [data-md-value='"+data+"']")
if(note)note.hidden=false})
feedback.hidden=false}})})