let intervalId;function startHeroAnimation(){if(intervalId){return;}
let words=['Open','Effortless','Custom'];let currentWordIndex=0;const highlightElement=document.querySelector('.mdx-hero__tag-highlight');if(!highlightElement){console.warn('Could not find the highlight element for the hero animation.');return;}
function changeWord(){highlightElement.classList.add('mdx-hero__tag-highlight--fade-out');setTimeout(()=>{currentWordIndex=(currentWordIndex+1)%words.length;highlightElement.textContent=words[currentWordIndex];highlightElement.classList.remove('mdx-hero__tag-highlight--fade-out');},500);}
intervalId=setInterval(changeWord,5000);}
function stopHeroAnimation(){if(intervalId){clearInterval(intervalId);intervalId=null;}}
document.addEventListener("DOMContentLoaded",function(){if(window.location.pathname==='/'||window.location.pathname===''){startHeroAnimation();}
const subscription=location$.subscribe((url)=>{setTimeout(()=>{if(url.pathname==='/'||url.pathname===''){startHeroAnimation();}else{stopHeroAnimation();}},1000);});});